  file: fs.createReadStream('./data/sales_data.csv'),
  purpose: 'assistants'
});

const customerDataFile = await openai.files.create({
  file: fs.createReadStream('./data/customer_demographics.xlsx'),
  purpose: 'assistants'
});

const marketDataFile = await openai.files.create({
  file: fs.createReadStream('./data/market_trends.json'),
  purpose: 'assistants'
});

// Create vector store for file search
const vectorStore = await openai.beta.vectorStores.create({
  name: "Sales Analysis Dataset",
  metadata: {
    project: "q4-sales-analysis",
    data_period: "2024-q1-q4"
  }
});

// Add files to vector store
const fileBatch = await openai.beta.vectorStores.fileBatches.create(
  vectorStore.id,
  {
    file_ids: [salesDataFile.id, customerDataFile.id, marketDataFile.id]
  }
);

// Update assistant with vector store
const updatedAssistant = await openai.beta.assistants.update(assistant.id, {
  tool_resources: {
    file_search: {
      vector_store_ids: [vectorStore.id]
    }
  }
});

console.log(`Dataset uploaded and configured for analysis`);

// Create analysis thread
const thread = await openai.beta.threads.create({
  metadata: {
    analysis_type: "sales-performance",
    priority: "high"
  }
});

// Initial data exploration request
const explorationMessage = await openai.beta.threads.messages.create(thread.id, {
  role: "user",
  content: `Please perform a comprehensive data exploration of our sales dataset:

1. **Data Quality Assessment**:
   - Load and examine all uploaded datasets
   - Check for missing values, duplicates, and outliers
   - Validate data types and formats
   - Assess data completeness and quality

2. **Descriptive Statistics**:
   - Generate summary statistics for all numerical variables
   - Create frequency distributions for categorical variables
   - Identify key patterns and initial insights

3. **Data Visualization**:
   - Create histograms and box plots for key metrics
   - Generate correlation heatmaps
   - Show time series plots if temporal data exists

4. **Initial Findings**:
   - Highlight any data quality issues
   - Identify interesting patterns or anomalies
   - Suggest areas for deeper analysis

Please provide both statistical output and clear explanations suitable for stakeholders.`
});

// Run the exploration
const run = await openai.beta.threads.runs.create(thread.id, {
  assistant_id: assistant.id,
  instructions: "Focus on providing actionable insights and clear visualizations. Include statistical significance where relevant."
});

console.log(`Started data exploration analysis: ${run.id}`);

// Function to monitor run completion and retrieve results
async function monitorAnalysisProgress(threadId, runId) {
  let run = await openai.beta.threads.runs.retrieve(threadId, runId);

  while (run.status === 'in_progress' || run.status === 'queued') {
    console.log(`Analysis status: ${run.status}`);
    await new Promise(resolve => setTimeout(resolve, 2000));
    run = await openai.beta.threads.runs.retrieve(threadId, runId);
  }

  if (run.status === 'completed') {
    console.log('âœ… Analysis completed successfully');

    // Retrieve all messages from the thread
    const messages = await openai.beta.threads.messages.list(threadId, {
      order: 'asc'
    });

    // Extract analysis results
    const analysisResults = {
      exploration: [],
      statistical_analysis: [],
      visualizations: [],
      reports: [],
      files_generated: []
    };

    for (const message of messages.data) {
      if (message.role === 'assistant') {
        // Extract text content
        const textContent = message.content
          .filter(content => content.type === 'text')
          .map(content => content.text.value);

        // Extract file attachments (charts, reports, etc.)
        const fileAttachments = message.attachments || [];

        analysisResults.reports.push({
          timestamp: message.created_at,
          content: textContent,
          files: fileAttachments
        });

        // Track generated files
        fileAttachments.forEach(attachment => {
          analysisResults.files_generated.push({
            file_id: attachment.file_id,
            tools: attachment.tools
          });
        });
      }
    }

    return analysisResults;
  } else {
    console.error(`âŒ Analysis failed with status: ${run.status}`);
    if (run.last_error) {
      console.error('Error details:', run.last_error);
    }
    throw new Error(`Analysis failed: ${run.status}`);
  }
}

