// Process multiple datasets in parallel
const datasets = ['sales_q1.csv', 'sales_q2.csv', 'sales_q3.csv', 'sales_q4.csv'];
const analysisPromises = datasets.map(async (dataset) => {
  const thread = await openai.beta.threads.create();
  const message = await openai.beta.threads.messages.create(thread.id, {
    role: 'user',
    content: `Analyze ${dataset} and provide quarterly insights`
  });
  return openai.beta.threads.runs.create(thread.id, { assistant_id: assistant.id });
});

const results = await Promise.all(analysisPromises);

